window.onload = function () {

    const show = elem => {
        elem.style.display = 'block';
    }
    const hide = elem => {
        elem.style.display = 'none';
    }

    const toggle = elem => {
        if (window.getComputedStyle(elem).display === 'block') {
            hide(elem);
            return;
        } 
        show(elem);
    }

    const mobileMenu = document.querySelector('.mobile-menu-wrapper');
    const openMobileMenuBtn = document.querySelector('.menu-btn');
    const closeMobileMenuBtn = document.querySelector('.close-menu');
    const menuSafeArea = document.querySelector('.menu-safe-area');

    openMobileMenuBtn.addEventListener('click', () => toggle(mobileMenu));
    closeMobileMenuBtn.addEventListener('click', () => toggle(mobileMenu));
    menuSafeArea.addEventListener('click', () => toggle(mobileMenu));
}